// 여러 가지 참조 가능. canvas components, Webcam
import React, { useRef, useEffect } from "react";
import "./App.css"; 
import * as tf from "@tensorflow/tfjs";
import * as faceLandmarksDetection from "@tensorflow-models/face-landmarks-detection";
import '@tensorflow/tfjs-backend-webgl';
import Webcam from "react-webcam";
import { drawMesh } from "./utilities"; 

function App() {
  //  Setup References
  const webcamRef = useRef(null);
  const canvasRef = useRef(null);

  //  Load facemeshs
  // tensorflow.js에서 model을 load
  const runFaceLandmarksDetection = async () => {
    const net = await faceLandmarksDetection.load(faceLandmarksDetection.SupportedPackages.mediapipeFacemesh);
    // detect fucntion 호출
    // 100ms마다 detect function 실행
    setInterval(()=>{
      detect(net);
    }, 10);
  };

  //  Detect function 
  const detect = async (net) => {
    // webcam이 설정되었는지 확인하는 조건문
    if(
      typeof webcamRef.current !== "undefined" &&
      webcamRef.current !== null &&
      webcamRef.current.video.readyState === 4
    ) {
      // Get Video Properties
      const video = webcamRef.current.video;
      const videoWidth = webcamRef.current.video.videoWidth;
      const videoHeight = webcamRef.current.video.videoHeight;

      // Set video width
      webcamRef.current.video.width = videoWidth;
      webcamRef.current.video.height = videoHeight;

      // Set canvas width
      canvasRef.current.width = videoWidth;
      canvasRef.current.hegiht = videoHeight;

      // Make detections
      // webcam으로 부터 받은 video 정보를 인자로 넘긴다.
      const face = await net.estimateFaces({input: video});
      console.log(face);

      // Get canvas context for drawing
      // 얼굴에 선과 점을 그린다.
      const ctx = canvasRef.current.getContext("2d");
      //drawMesh(face, ctx);
      requestAnimationFrame(() => {drawMesh(face, ctx)});
    }
  };

  useEffect(()=>{runFaceLandmarksDetection()}, []);

  return (
    <div className="App">
      <header className="App-header">
      <Webcam 
      ref={webcamRef} 
      style={{
          position: "absolute",
          marginLeft: "auto",
          marginRight: "auto",
          left: 0,
          right: 0,
          textAlign: "center",
          zIndex: 9,
          width: 640,
          height: 480,
        }} 
      />
      <canvas 
      ref={canvasRef}
      style={{
          position: "absolute",
          marginTop: "100px",
          marginBottom: "100px",
          marginLeft: "auto",
          marginRight: "auto",
          left: 0,
          right: 0,
          textAlign: "center",
          zIndex: 9,
          width: 640,
          height: 480,
        }} 
      />
      </header>
    </div>
  );
}

export default App;
